import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import {faShoppingCart } from '@fortawesome/free-solid-svg-icons'
const Car = (props) => {
    const { brandName, price, color, length, image } = props.car
    return (
        <div style={{'width': '250px'}}>
            <div>
                <img className="w-50" src={image} alt="" />
            </div>
            <div>
                <p>Name : {brandName} </p>
                <p>Price: <span>à§³</span> {price}</p>
                <p>Color:{color} </p>
                <p>Length:{length} </p>
                <button className="btn btn-secondary" onClick={() => props.handleCart(props.car)}>
                    <FontAwesomeIcon icon={faShoppingCart} />Add To Cart </button>
            </div>
        </div>
    );
};

export default Car;
