import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import {faShoppingCart } from '@fortawesome/free-solid-svg-icons'
const Car = (props) => {
    const { brandName, price, color, length, image,isRemoteStart } = props.car
    // style={{'marginRight': '35px'}}
    return (
        <div className="border shadow p-3 mb-5 bg-body rounded" style={{'width': '285px','marginRight': '65px'}}>
            <div>
                <img className="w-100 mb-3" src={image} alt="" />
            </div>
            <div className="text-center">
                <p>Name : {brandName} </p>
                <p>Price: <span>à§³</span> {price}</p>
                <p>Color:{color} </p>
                <p>IsRemoteStart : {isRemoteStart}</p>
                <p>Length:{length} </p>
                <button className="btn btn-secondary" onClick={() => props.handleCart(props.car)}>
                    <FontAwesomeIcon icon={faShoppingCart} />Add To Cart </button>
            </div>
        </div>
    );
};
export default Car;
