import React from 'react';
import Name from '../Name/Name';

const Cart = (props) => {
    let total = 0;
    let count = 0;
    let nameArray = []
    let myArr = props.cart
    console.log(myArr);
    for (const a of myArr) {
        count = count + 1;
        total = total + a.price
        nameArray.push(a.brandName)
    }
    return (
        <div>

            <p>Total Added:{count} </p>
            <p>Total Cost: <span>à§³ </span>{total} </p>
            {
                <Name nameArray={nameArray}></Name>
            }
        </div>
    );
};

export default Cart;
